/**
 * @description: Array递归遍历验证ID
 * @param {arr} 数组参数
 * @param {name}  验证ID
 * @return: recursive
 */
export function recursive(arr, name) {
  return arr.filter(item => {
    if (name === '0') {
      this.categorySuperiorContent = '无上级分类'
      return item
    }
    if (item.pid === name) {
      this.categorySuperiorContent = item.name
      return item
    }
    if (item.childrens !== null) {
      return recursive.call(this, item.childrens, name)
    }
  })
}

/**
 * @description: 短信验证码倒计时
 * @param {Number} 倒计时
 * @method factory
 * @return: factorySendCode
 * @author 余亚龙 2019/03/24
 */
export function factorySendCode(num) {
  if (this.remaining === 0) {
    this.disabled = false
    this.remaining = num
    this.btnText = '重新发送'
    this.btnType = 'primary'
    return false
  } else {
    this.disabled = true
    this.btnText = '重新发送' + this.remaining
    this.remaining--
    setTimeout(() => {
      factorySendCode.call(this, num)
    }, 1000)
  }
}
