import { post } from '@/api/axios.js'
import api from '@/api/index.js'

/**
 * @description: 查看渠道商的原材料列表
 * @param {type}
 * @return: bomSearch
 */
export function bomSearch() {
  return post(api.bomearch).then(res => {
    if (res.data.code === 0) {
      console.log(res.data.data)
    }
  }).catch(err => {
    console.log(err)
  })
}
