import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    custEditableInfo: '',
    getCustDetailInfo: '',
    workbench: false,
    getUsername: ''
  },
  getters: {},
  mutations: {
    setUsername(state, username) {
      state.getUsername = localStorage.getItem('NICK')
    },
    editableInfo(state, editableInfo) {
      state.custEditableInfo = editableInfo
    },
    setCustDetailInfo(state, custDetailInfo) {
      state.getCustDetailInfo = custDetailInfo
    },
    setWorkbench(state, payload) {
      state.workbench = payload
    }
  },
  actions: {
    // NICK
    username({ commit }, username) {
      commit('setUsername', localStorage.setItem('NICK', username))
    },
    // 客户管理新建联系人信息
    editableInfo({ commit }, viewCategoryInfo) {
      commit('editableInfo', viewCategoryInfo)
    },
    // 客户查看详情
    custDetailInfo({ commit }, custDetailInfo) {
      commit('setCustDetailInfo', custDetailInfo)
    }
  }
})
