import { service as axios } from './axios'
import qs from 'qs'

/**
 * get方法，对应get请求
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function get(url, parameter) {
  return axios({
    url: url,
    method: 'get',
    params: parameter,
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  })
}

/**
 * post方法，对应post请求 form-data
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function post(url, parameter) {
  return axios({
    url: url,
    method: 'post',
    data: qs.stringify(parameter),
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  })
}

/**
 * delete方法，对应deleted请求
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function deleted(url, parameter) {
  return axios({
    url: url,
    method: 'delete',
    data: parameter,
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  })
}

/**
 * delete方法，对应deleted Json请求
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function deletedJson(url, parameter) {
  return axios({
    url: url,
    method: 'delete',
    data: parameter,
    headers: {
      'Content-Type': 'application/json;charset=utf-8'
    }
  })
}

/**
 * post方法
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function postJson(url, parameter) {
  return axios({
    url: url,
    method: 'post',
    data: JSON.stringify(parameter),
    headers: {
      'Content-Type': 'application/json;charset=utf-8'
    }
  })
}

/**
 * @description: put方法
 * @param {String} url [请求url地址]
 * @param {Object} parameter [请求时携带的参数]
 * @return: put
 */
export function put(url, parameter) {
  return axios({
    url: url,
    method: 'put',
    data: JSON.stringify(parameter),
    headers: {
      'Content-Type': 'application/json;charset=utf-8 '
    }
  })
}

/**
 * @description: POST
 * @param {String} url [请求url地址]
 * @param {Object} parameter [请求时携带的参数]
 * @return: uploadPost
 */
export function uploadPost(url, parameter) {
  return axios({
    url: url,
    method: 'post',
    data: parameter,
    headers: {
      'Content-Type': 'multipart/form-data'
    }
  })
}
