import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    custEditableInfo: '',
    getCustDetailInfo: '',
    workbench: false,
    navPermissionArr: []
  },
  getters: {
    getNavPermission(state) {
      return state.navPermissionArr
    }
  },
  mutations: {
    setPermission(state, navPermission) {
      state.navPermissionArr = navPermission
    },
    editableInfo(state, editableInfo) {
      state.custEditableInfo = editableInfo
    },
    setCustDetailInfo(state, custDetailInfo) {
      state.getCustDetailInfo = custDetailInfo
    },
    setWorkbench(state, payload) {
      state.workbench = payload
    }
  },
  actions: {
    // 列表权限
    navPermission({ commit }, navPermission) {
      commit('setPermission', navPermission)
    },
    // 客户管理新建联系人信息
    editableInfo({ commit }, viewCategoryInfo) {
      commit('editableInfo', viewCategoryInfo)
    },
    // 客户查看详情
    custDetailInfo({ commit }, custDetailInfo) {
      commit('setCustDetailInfo', custDetailInfo)
    }
  }
})
