import { getGoodsClass } from '../../basic_lib/index';
import { springType } from '../../basic_lib/column';
import { mixinBasic, mixinIsEdit } from '@/components/tools/mixin/mixin';
export default {
  name: 'ProductInfo',
  mixins: [mixinBasic, mixinIsEdit],
  data: function data() {
    return {
      tabGoodsInfo: this.$form.createForm(this),
      options: [],
      springType: springType,
      isSpring: false
    };
  },
  created: function created() {
    // 产品分类
    getGoodsClass.call(this);
  },
  methods: {
    // 产品分类
    onChangeGoodsClass: function onChangeGoodsClass(value) {
      console.log(value);
    },
    // 产品类别
    onChangeProductClass: function onChangeProductClass(value) {
      if (value === undefined) {
        this.isSpring = false;
        return;
      }

      this.isSpring = true;
    },
    onSelectProductClass: function onSelectProductClass(value) {
      if (value === '0') {
        this.isSpring = true;
        return;
      }

      this.isSpring = false;
    }
  }
};
