/**
 * @description: 引入一些公共的资源
 */
// import baseURL from '@/api/config'
import { get, postJson } from '@/api/axios.js'
import api from '@/api/index.js'
import { status, moneyType, instorageType, puStorageStatus } from './menu'
const url = '/api/ic/user/file/uploadFile'

/**
 * @description: mixinUpload Basic
 * @param {String} url [图片上传地址]
 * @param {Array} fileList [图片上传列表]
 * @param {Object} headers [图片上传所需参数]
 * @param {Object} uploadFile [拷贝图片上传的地址]
 * @param {Boolean} previewVisible [是否查看图片大图]
 * @param {String} previewImage [查看图片大图的地址]
 * @return: mixinUpload
 */
export const mixinUpload = {
  data() {
    return {
      url,
      fileList: [],
      headers: {
        'AUTH-TOKEN': localStorage.getItem('ACCESS_TOKEN'),
        companyId: localStorage.getItem('companyId')
      },
      uploadFile: [],
      previewVisible: false,
      previewImage: ''
    }
  },
  methods: {
    // 图片上传相关
    handlePreview(file) {
      this.previewImage = file.url || file.thumbUrl
      this.previewVisible = true
    },
    // 图片上传/修改时的操作
    handleChange({ fileList }) {
      this.fileList = fileList
      const uploadFile = []
      for (const i in fileList) {
        if (fileList[i].response) {
          uploadFile.push(fileList[i].response.data)
        } else {
          uploadFile.push(fileList[i].url)
        }
      }
      this.uploadFile = uploadFile
    },
    handleCancel() {
      this.previewVisible = false
    }
  }
}

/**
 * @description: 选择器的下拉加载
 * @return: mixinCurrentPageChange
 */
export const mixinCurrentPage = {
  methods: {
    getCurrentPageChange(e) {
      const boxHeigth = e.target.scrollHeight - 1
      let height = getComputedStyle(document.querySelector('.ant-select-dropdown-menu')).maxHeight
      height = height.substring(0, height.length - 2)
      const box = {
        scrollTop: e.target.scrollTop
      }
      const dropDownHeight = (parseFloat(height) + box.scrollTop)
      const result = (dropDownHeight > boxHeigth)
      return result
    }
  }
}

/**
 * @description: 获取产品编码 产品规格 产品材质
 * @param {Number} codeCurrentPage 产品编码分页
 * @param {Number} specCurrentPage 产品规格分页
 * @param {Number} czCurrentPage 产品材质分页
 * @param {Array} odSkuCodeList 产品编码
 * @param {Array} odSpecList 产品规格
 * @param {Array} textures 产品材质
 */
export const mixinGetProductAbout = {
  data() {
    return {
      codeCurrentPage: 1,
      specCurrentPage: 1,
      czCurrentPage: 1,
      odSkuCodeList: [],
      odSpecList: [],
      textures: []
    }
  },
  methods: {
    // 列表商家所有商品的商品编码
    getListCompanyGoodsCode(currentPage = 1, pageSize = 20) {
      get(api.listCompanyGoodsCode, { currentPage, pageSize }).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          const odSkuCodeList = this.odSkuCodeList
          this.odSkuCodeList = odSkuCodeList.concat(data).filter(item => item !== null)
          if (this.codeCurrentPage < Number(res.data.data.pages)) {
            this.codeCurrentPage += 1
          }
        }
      })
    },
    // 列表商家所有弹簧的规格
    getListCompanySpringSpecs(currentPage = 1, pageSize = 20) {
      get(api.getListCompanySpringSpecs, { currentPage, pageSize }).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          const odSpecList = this.odSpecList
          this.odSpecList = odSpecList.concat(data).filter(item => item !== null)
          if (this.specCurrentPage < Number(res.data.data.pages)) {
            this.specCurrentPage += 1
          }
        }
      })
    },
    // 列表商家所有弹簧的材质
    getListCompanySpringCz(currentPage = 1, pageSize = 20) {
      get(api.getListCompanySpringCz, { currentPage, pageSize }).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          const textures = this.textures
          this.textures = textures.concat(data).filter(item => item !== null)
          if (this.codeCurrentPage < Number(res.data.data.pages)) {
            this.czCurrentPage += 1
          }
        }
      })
    },
    // 列表所有采购材质
    getFactoryMaterialRequireCz(currentPage = 1, pageSize = 20) {
      get(api.getFactoryMaterialRequireCz, { currentPage, pageSize }).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          const textures = this.textures
          this.textures = textures.concat(data).filter(item => item !== null)
          if (this.codeCurrentPage < Number(res.data.data.pages)) {
            this.czCurrentPage += 1
          }
        }
      })
    },
    // 列表所有采购规格
    getFactoryMaterialRequireSpec(currentPage = 1, pageSize = 20) {
      get(api.getFactoryMaterialRequireSpec, { currentPage, pageSize }).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          const odSpecList = this.odSpecList
          this.odSpecList = odSpecList.concat(data).filter(item => item !== null)
          if (this.specCurrentPage < Number(res.data.data.pages)) {
            this.specCurrentPage += 1
          }
        }
      })
    },
    // 工单分单-工厂订单商品材质
    getFactoryPolicyOrderCz(currentPage = 1, pageSize = 20) {
      postJson(api.getFactoryPolicyOrderCz, { currentPage, pageSize }).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          const textures = this.textures
          this.textures = textures.concat(data).filter(item => item !== null)
          if (this.codeCurrentPage < Number(res.data.data.pages)) {
            this.czCurrentPage += 1
          }
        }
      })
    },
    // 工单分单-工厂订单商品规格
    getFactoryPolicyOrderSpec(currentPage = 1, pageSize = 20) {
      postJson(api.getFactoryPolicyOrderSpec, { currentPage, pageSize }).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          const odSpecList = this.odSpecList
          this.odSpecList = odSpecList.concat(data).filter(item => item !== null)
          if (this.specCurrentPage < Number(res.data.data.pages)) {
            this.specCurrentPage += 1
          }
        }
      })
    }
  }
}

/**
 * @description: 获取公司所有客户 以及客户编码
 */
export const getListCompanyCusts = {
  data() {
    return {
      customerPage: 1,
      listCompanyCusts: []
    }
  },
  created() {
    this.getListCompanyCusts(this.customerPage)
  },
  methods: {
    getListCompanyCusts(currentPage = 1, pageSize = 20) {
      get(api.getListCompanyCusts, { currentPage, pageSize }).then(res => {
        const data = res.data.data.records
        this.listCompanyCusts = this.listCompanyCusts.concat(data)
        if (this.customerPage < Number(res.data.data.pages)) {
          this.customerPage += 1
        }
      })
    }
  }
}

/**
 * @description: 订单状态过滤
 */
export const statusChange = {
  filters: {
    // 订单状态
    stateFilter(value) {
      let state
      status.forEach(item => {
        if (item.value === value) {
          state = item
        }
      })
      if (state) {
        return state.label
      } else {
        return ''
      }
    },
    // 金币转换
    moneyFilter(val) {
      let dataMoney
      moneyType.forEach(item => {
        if (item.value === val) {
          dataMoney = item
        }
      })
      if (dataMoney) {
        return dataMoney.label
      } else {
        return ''
      }
    },
    // 入库类型
    puStorageType(val) {
      let storageType
      instorageType.forEach(item => {
        if (item.value === val) {
          storageType = item
        }
      })
      if (storageType) {
        return storageType.label
      } else {
        return ''
      }
    },
    // 入库状态
    putStorageStatus(val) {
      let storageStatus
      puStorageStatus.forEach(item => {
        if (item.key === val) {
          storageStatus = item
        }
      })
      if (storageStatus) {
        return storageStatus.value
      } else {
        return ''
      }
    }
  }
}
