import Vue from 'vue'
import Router from 'vue-router'
import Home from './views/Home.vue'

Vue.use(Router)

export default new Router({
  routes: [
    {
      path: '/',
      component: Home,
      children: [
        // 客户管理
        { path: '/', redirect: '/permission' },
        { name: 'customer', path: '/crm/cust', component: () => import('./views/customer/Customer.vue'), meta: { breads: ['客户关系管理', '客户'] } },
        // 商城
        { path: '/mall/cashier', name: 'cashier', component: () => import('./views/mall/Cashier.vue'), meta: { breads: ['商城', '基础设置', '支付方式'] } },
        { path: '/mall/delivery', name: 'technologyManage', component: () => import('./views/mall/Delivery.vue'), meta: { breads: ['商城', '基础设置', '配送方式'] } },
        // 产品管理
        { path: '/pdm/category', name: 'gathering', component: () => import('./views/product/category/Category.vue'), meta: { breads: ['产品管理', '产品管理', '产品分类'] } },
        { path: '/pdm/catattr', name: 'payment', component: () => import('./views/product/catattr/Catattr.vue'), meta: { breads: ['产品管理', '产品管理', '属性分类'] } },
        { path: '/pdm/goods', name: 'refund', component: () => import('./views/product/goods/Goods.vue'), meta: { breads: ['产品管理', '产品管理', '产品列表'] } },
        { path: '/sale/order', name: 'order', component: () => import('./views/sale/orderList/Order.vue'), meta: { breads: ['销售列表', '产品列表'] } },
        {
          path: '/permission', component: () => import('./views/permission/Permission.vue')
        }
      ]
    },
    {
      path: '/login',
      name: 'login',
      component: () => import(/* webpackChunkName: "about" */ './views/user/Login.vue')
    },
    {
      path: '/register',
      name: 'register',
      // route level code-splitting
      // this generates a separate chunk (about.[hash].js) for this route
      // which is lazy-loaded when the route is visited.
      component: () => import(/* webpackChunkName: "about" */ './views/user/Register.vue')
    },
    {
      path: '/retrieve',
      name: 'retrieve',
      component: () => import(/* webpackChunkName: "about" */ './views/user/Retrieve.vue')
    },
    {
      path: '/403',
      name: '403',
      component: () => import(/* webpackChunkName: "about" */ './views/exception/403.vue')
    },
    {
      path: '/404',
      name: '404',
      component: () => import(/* webpackChunkName: "about" */ './views/exception/404.vue')
    },
    {
      path: '/500',
      name: '500',
      component: () => import(/* webpackChunkName: "about" */ './views/exception/500.vue')
    },
    {
      path: '*',
      redirect: '/permission'
    }
  ]
})
