/* eslint-disable no-unused-vars */
import { get } from '@/api/axios.js'
import api from '@/api/index.js'

// 查询公司的所有人(客户经理)
export function getCompanyAllUsers() {
  get(api.getCompanyAllUsers).then(res => {
    var companyAllUsers = res.data.data
    var ocustAmaldarList = []
    for (const i in companyAllUsers) {
      ocustAmaldarList.push(companyAllUsers[i].name)
    }
    this.ocustAmaldarList = ocustAmaldarList
  })
}
// 列表商家对客户的定制商品基本信息
export function getListCustomerGoods(custId, currentPage = 1, pageSize = 20) {
  get(api.getListCustomerGoods, { custId, currentPage, pageSize }).then(res => {
    var listCustomerGoods = res.data.data.records
    var odSkuCodeList = []
    var odSpecList = []
    for (const i in listCustomerGoods) {
      if (listCustomerGoods[i].code && this.odSkuCodeList.indexOf(listCustomerGoods[i].code) === -1) {
        odSkuCodeList.push(listCustomerGoods[i].code)
      }
      if (listCustomerGoods[i].spec && this.odSpecList.indexOf(listCustomerGoods[i].spec) === -1) {
        odSpecList.push(listCustomerGoods[i].spec)
      }
    }
    if (this.listCustomerGoods) {
      this.listCustomerGoods = this.listCustomerGoods.concat(listCustomerGoods)
    } else {
      this.listCustomerGoods = listCustomerGoods
    }
    if (this.odSkuCodeList) {
      this.odSkuCodeList = this.odSkuCodeList.concat(odSkuCodeList)
    } else {
      this.odSkuCodeList = odSkuCodeList
    }
    if (this.odSpecList) {
      this.odSpecList = this.odSpecList.concat(odSpecList)
    } else {
      this.odSpecList = odSpecList
    }
    this.customerGoods += 1
  })
}
// 获取公司所有客户
export function getListCompanyCusts(currentPage = 1, pageSize = 20) {
  get(api.getListCompanyCusts, { currentPage, pageSize }).then(res => {
    var listCompanyCusts = res.data.data.records
    var ocustNameList = []
    var ocustCodeList = []
    for (const i in listCompanyCusts) {
      if (listCompanyCusts[i].custName && this.ocustNameList.indexOf(listCompanyCusts[i].custName) === -1) {
        ocustNameList.push(listCompanyCusts[i].custName)
      }
      if (listCompanyCusts[i].custCode && this.ocustCodeList.indexOf(listCompanyCusts[i].custCode) === -1) {
        ocustCodeList.push(listCompanyCusts[i].custCode)
      }
    }
    if (this.listCompanyCusts) {
      this.listCompanyCusts = this.listCompanyCusts.concat(listCompanyCusts)
    } else {
      this.listCompanyCusts = listCompanyCusts
    }
    if (this.ocustNameList) {
      this.ocustNameList = this.ocustNameList.concat(ocustNameList)
    } else {
      this.ocustNameList = ocustNameList
    }
    if (this.ocustCodeList) {
      this.ocustCodeList = this.ocustCodeList.concat(ocustCodeList)
    } else {
      this.ocustCodeList = ocustCodeList
    }
    this.customerPage += 1
  })
}
// 列表商家所有商品基本信息
export function getListCompanyGoods(currentPage = 1, pageSize = 20) {
  get(api.getListCompanyGoods, { currentPage, pageSize }).then(res => {
    var listCompanyGoods = res.data.data.records
    var odSkuCodeList = []
    var odSpecList = []
    for (const i in listCompanyGoods) {
      if (listCompanyGoods[i].code && this.odSkuCodeList.indexOf(listCompanyGoods[i].code) === -1) {
        odSkuCodeList.push(listCompanyGoods[i].code)
      }
      if (listCompanyGoods[i].spec && this.odSpecList.indexOf(listCompanyGoods[i].spec) === -1) {
        odSpecList.push(listCompanyGoods[i].spec)
      }
    }
    if (this.odSkuCodeList) {
      this.odSkuCodeList = this.odSkuCodeList.concat(odSkuCodeList)
    } else {
      this.odSkuCodeList = odSkuCodeList
    }
    if (this.odSpecList) {
      this.odSpecList = this.odSpecList.concat(odSpecList)
    } else {
      this.odSpecList = odSpecList
    }
    this.companyGoods += 1
  })
}
