import { get } from '@/api/axios.js';
import api from '@/api/index.js';
import { columns } from './basic_lib/columns';
import { mixinBasic, mixinDrawer } from '@/components/tools/mixin/mixin';
import purchaseInfo from './child_info/purchaseInfo';
import classification from '@/components/list_classification/classification';
import inputSearch from './other/inputSearch';
import statusStep from '@/components/listStatus/step';
export default {
  mixins: [mixinBasic, mixinDrawer],
  components: {
    purchaseInfo: purchaseInfo,
    classification: classification,
    inputSearch: inputSearch,
    statusStep: statusStep
  },
  data: function data() {
    return {
      category: 'SUPPLIER',
      columns: columns,
      data: [],
      selectedRowKeys: [],
      pagination: {
        total: 0,
        pageSize: 15,
        defaultCurrent: 1,
        showTotal: function showTotal(total) {
          return "\u5171".concat(total, "\u6761");
        },
        showQuickJumper: true,
        showSizeChanger: true,
        pageSizeOptions: ['10', '15', '30', '50', '100']
      },
      supplierId: '',
      purchasePostBean: {},
      prchaseDetial: {},
      statusAction: false
    };
  },
  created: function created() {
    this.getFactoryPrchase();
  },
  computed: {
    rowSelection: function rowSelection() {
      var selectedRowKeys = this.selectedRowKeys;
      return {
        selectedRowKeys: selectedRowKeys,
        onChange: this.onChangeTab,
        hideDefaultSelections: false
      };
    },
    getMenuPermission: function getMenuPermission() {
      return this.$store.state.menuPermission;
    }
  },
  methods: {
    onChangeTab: function onChangeTab(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys;
      this.selectedRows = selectedRows;
    },
    // 翻页
    onChangePage: function onChangePage(pagination, filters, sorter) {
      this.pagination.pageSize = pagination.pageSize;
      this.getFactoryPrchase(pagination.current);
    },
    // 选择供应商 查看采购列表
    purchaseListId: function purchaseListId(id) {
      this.supplierId = id;
      this.getFactoryPrchase();
    },
    getFactoryPrchase: function getFactoryPrchase() {
      var _this = this;

      var currentPage = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : 1;
      this.loading = true;
      var data = this.purchasePostBean;
      data.supplierId = this.supplierId;
      data.currentPage = currentPage;
      data.pageSize = this.pagination.pageSize;
      get(api.getFactoryMaterialSupplierList, data).then(function (res) {
        if (res.data.code === 0) {
          _this.pagination.total = Number(res.data.data.total);
          _this.data = res.data.data.records;
        } else {
          _this.$message.error(res.data.msg);
        }

        _this.loading = false;
      }).catch(function () {
        _this.$message.error('系统错误');

        _this.loading = false;
      });
    },
    // 打开抽屉
    showDrawer: function showDrawer(type, record) {
      switch (type) {
        case 'factoryOdNumber':
          this.getProcurementDetial(record.id);
          break;
      }
    },
    // 获取渠道商采购详情
    getProcurementDetial: function getProcurementDetial(id) {
      var _this2 = this;

      if (this.getMenuPermission.includes('DETAIL')) {
        get(api.getFactoryMaterialSupplierListDetail + "".concat(id)).then(function (res) {
          if (res.data.code === 0) {
            var data = res.data.data;
            data.toSkuInfo = JSON.parse(data.toSkuInfo);
            _this2.prchaseDetial = data;
            _this2.visible = true;

            _this2.$store.commit('setPromptTitle', {
              title: '采购详情',
              isEdit: false
            });
          } else {
            _this2.$message.error(res.data.msg);
          }
        });
      } else {
        this.$message.error('权限不足');
      }
    },
    // 关闭抽屉
    isDrawer: function isDrawer(val) {
      if (val) {
        this.getFactoryPrchase();
      }

      this.visible = false;
    },
    // 按条件搜索采购列表
    dataChange: function dataChange(val) {
      this.purchasePostBean.factoryOdNumber = val.odOrderNumber;
      this.purchasePostBean.spec = val.spec;
      this.purchasePostBean.endTime = val.endTime;
      this.purchasePostBean.startTime = val.startTime;
      this.getFactoryPrchase();
    }
  }
};
