import axios from 'axios'
import qs from 'qs'
import notification from 'ant-design-vue/es/notification'
import router from '@/router/router'

// 统一配置
// axios.defaults.baseURL = 'http://192.168.1.110:9001'
axios.defaults.baseURL = 'http://192.168.1.109:9001'
axios.defaults.timeout = 10000
// axios.defaults.headers.post['Content-Type'] = 'Content-Type: application/x-www-form-urlencoded'

// 请求拦截器
axios.interceptors.request.use(
  config => {
    // 每次发送请求之前判断vuex中是否存在token
    // 如果存在，则统一在http请求的header都加上token，这样后台根据token判断你的登录情况
    // 即使本地存在token，也有可能token是过期的，所以在响应拦截器中要对返回状态进行判断
    const token = localStorage.getItem('ACCESS_TOKEN')
    const companyId = localStorage.getItem('companyId')
    if (token) {
      config.headers['AUTH-TOKEN'] = token
    }
    if (companyId) {
      config.headers['companyId'] = companyId
    }
    return config
  },
  error => {
    return Promise.error(error)
  })

// http response 拦截器
axios.interceptors.response.use(
  response => {
    if (response.data.code === 101) {
      localStorage.clear()
      router.replace('/login')
      notification.error({
        message: response.data.msg
      })
    }
    if (response.data.code === 102) {
      notification.error({
        message: response.data.msg
      })
    }
    return response
  },
  error => {
    if (error.response) {
      const data = error.response.data
      const token = localStorage.getItem('ACCESS_TOKEN')
      console.log(data)
      if (error.response.status === 403) {
        notification.error({
          message: '登录超时',
          description: data.msg
        })
        router.replace('/403')
        // return
      }
      if (error.response.status === 404) {
        notification.error({
          message: '客户端错误',
          description: data.msg
        })
        router.replace('/403')
        // return
      }
      if (error.response.status === 500) {
        notification.error({
          message: '请求超时',
          description: '请返回重新登录'
        })
        if (token) {
          // localStorage.removeItem('ACCESS_TOKEN')
          setTimeout(() => {
            router.replace('/500')
          }, 500)
        }
        // return
      }
    }
    return Promise.reject(error)
  }
)

/**
 * get方法，对应get请求
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function get(url, parameter) {
  return axios({
    url: url,
    method: 'get',
    params: parameter,
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  })
}

/**
 * post方法，对应post请求 form-data
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function post(url, parameter) {
  return axios({
    url: url,
    method: 'post',
    data: qs.stringify(parameter),
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  })
}

/**
 * delete方法，对应post请求
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function deleted(url, parameter) {
  return axios({
    url: url,
    method: 'delete',
    data: parameter,
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
  })
}

/**
 * post方法
 * @param {String} url [请求的url地址]
 * @param {Object} params [请求时携带的参数]
 */
export function postJson(url, parameter) {
  return axios({
    url: url,
    method: 'post',
    data: JSON.stringify(parameter),
    headers: {
      'Content-Type': 'application/json;charset=utf-8 '
    }
  })
}

/**
 * @description: put方法
 * @param {String} url [请求url地址]
 * @param {Object} parameter [请求时携带的参数]
 * @return: put
 */
export function put(url, parameter) {
  return axios({
    url: url,
    method: 'put',
    data: JSON.stringify(parameter),
    headers: {
      'Content-Type': 'application/json;charset=utf-8 '
    }
  })
}

/**
 * @description: POST
 * @param {String} url [请求url地址]
 * @param {Object} parameter [请求时携带的参数]
 * @return: uploadPost
 */
export function uploadPost(url, parameter) {
  return axios({
    url: url,
    method: 'post',
    data: parameter,
    headers: {
      'Content-Type': 'multipart/form-data'
    }
  })
}
