 */
export const statusChange = {
  filters: {
    // 订单状态
    stateFilter(value) {
      let state
      status.forEach(item => {
        if (item.value === value) {
          state = item
        }
      })
      if (state) {
        return state.label
      } else {
        return ''
      }
    },
    // 金币转换
    moneyFilter(val) {
      let dataMoney
      moneyType.forEach(item => {
        if (item.value === val) {
          dataMoney = item
        }
      })
      if (dataMoney) {
        return dataMoney.label
      } else {
        return ''
      }
    },
    // 入库类型
    puStorageType(val) {
      let storageType
      instorageType.forEach(item => {
        if (item.value === val) {
          storageType = item
        }
      })
      if (storageType) {
        return storageType.label
      } else {
        return ''
      }
    },
    // 入库状态
    putStorageStatus(val) {
      let storageStatus
      puStorageStatus.forEach(item => {
        if (item.key === val) {
          storageStatus = item
        }
      })
      if (storageStatus) {
        return storageStatus.value
      } else {
        return ''
      }
    }
  }
}
