import api from '@/api/index.js'
import { get, postJson, put } from '@/api/axios.js'

/**
 * @description: 所有分类的所有属性列表
 * @param {Number} currentPage [页数]
 * @param {Number} pageSize  [每页显示数据条数]
 * @return getCategoryTree
 */
export function getCategoryTree(currentPage = 1, pageSize = 15) {
  this.loading = true
  return get(api.catattrList + currentPage + '/' + pageSize)
    .then(res => {
      const getList = res.data.data
      if (res.data.code === 0) {
        this.loading = false
        this.pagination.total = Number(getList.total)
        this.data = []
        getList.records.forEach(item => {
          this.data.push({
            id: item.id,
            catname: item.catname,
            name: item.name,
            custId: item.custId,
            code: item.code,
            remark: item.remark,
            childrens: item.childrens === null ? '' : item.childrens
          })
        })
        console.log(this.data)
      }
    })
    .catch(err => {
      console.log(err)
      this.loading = false
    })
}

/**
 * @description: 获取公司所有客户和公司自己
 * @param {type} null
 * @return: getListCompanyCustsAndCompanySelf
 */
export function getListCompanyCustsAndCompanySelf() {
  return get(api.listCompanyCustsAndCompanySelf).then(res => {
    const getData = res.data
    console.log(getData)
    if (getData.code === 0) {
      getData.data.forEach(item => {
        this.catattrCustData.push({
          id: item.id,
          custShortName: item.custShortName
        })
      })
      // console.log(this.catattrCustData)
    }
  }).catch(err => {
    console.log(err)
  })
}

/**
 * @description: 根据客户或者公司ID获取所有商品分类
 * @param {String} custId [客户ID]
 * @return getCustList
 */
export function getCustList(id) {
  return get(api.categoryCustList, { custId: id }).then(res => {
    const custlist = res.data.data
    console.log(custlist)
    if (res.data.code === 0) {
      this.options = custlist
    }
  }).catch(err => {
    console.log(err)
  })
}

/**
 * @description: 属性分类添加提交
 * @param {Object} params form表单字段
 * @return: catattrForm
 */
export function catattrForm(params) {
  return postJson(api.catattrSubmit, params).then(res => {
    console.log(res)
    if (res.data.code === 0) {
      this.loading = false
      this.loading = false
      this.$message.success('添加成功')
      this.$emit('isVisible', false)
      return false
    }
    this.loading = false
    this.$message.error(res.data.msg)
    this.$emit('isVisible', true)
  }).catch(err => {
    console.log(err)
  })
}

/**
 * @description: 属性分类Tab列表点击查看详情
 * @param {String} catAttrId 属性名称ID
 * @return: getCategoryDetail
 */
export function getCatattrDetail(catAttrId) {
  return get(api.catattrDetail + catAttrId).then(res => {
    if (res.data.code === 0) {
      // console.log(res.data)
      const viewCatattrInfo = {
        id: res.data.data.id,
        custId: res.data.data.custId, // 客户ID
        catId: res.data.data.catId, // 属性ID
        name: res.data.data.name, // 属性name
        unit: res.data.data.unit, // 计量单位
        inputType: res.data.data.inputType, // 输入类型
        valType: res.data.data.valType, // 值类型
        required: res.data.data.required, // 是否必填
        searchProp: res.data.data.searchProp, // 是否搜索属性
        listProp: res.data.data.listProp, // 是否列表属性
        optionList: res.data.data.optionList, // 可选项
        remark: res.data.data.remark // 备注
      }
      this.viewCatattrInfo = viewCatattrInfo
      console.log(this.viewCatattrInfo)
    }
  }).catch(err => {
    console.log(err)
  })
}

/**
 * @description: 详情信息修改保存
 * @param {Object} params [Object]
 * @return: onSave
 */
export function onSave(params) {
  return put(api.catattrSubmit, params).then(res => {
    console.log(res)
    if (res.data.code === 0) {
      this.loading = false
      this.loading = false
      this.$message.success('保存成功')
      this.$emit('isVisible', false)
      return false
    }
    this.loading = false
    this.$message.error(res.data.msg)
    this.$emit('isVisible', true)
