/* eslint-disable no-unused-vars */
import { get } from '@/api/axios.js'
import api from '@/api/index.js'

// 查询公司的所有人(客户经理)
export function getCompanyAllUsers() {
  get(api.getCompanyAllUsers).then(res => {
    var companyAllUsers = res.data.data
    var ocustAmaldarList = []
    for (const i in companyAllUsers) {
      ocustAmaldarList.push(companyAllUsers[i].name)
    }
    this.ocustAmaldarList = ocustAmaldarList
  })
}
// 列表商家对客户的定制商品基本信息
export function getListCustomerGoods(custId) {
  get(api.getListCustomerGoods, { custId: custId }).then(res => {
    var listCustomerGoods = res.data.data
    var odSkuCodeList = []
    var odSpecList = []
    for (const i in listCustomerGoods) {
      odSkuCodeList.push(listCustomerGoods[i].code)
      odSpecList.push(listCustomerGoods[i].spec)
    }
    this.listCustomerGoods = listCustomerGoods
    this.odSkuCodeList = odSkuCodeList
    this.odSpecList = odSpecList
  })
}
// 获取公司所有客户
export function getListCompanyCusts() {
  get(api.getListCompanyCusts).then(res => {
    var listCompanyCusts = res.data.data
    var ocustNameList = []
    var ocustCodeList = []
    for (const i in listCompanyCusts) {
      ocustNameList.push(listCompanyCusts[i].custShortName)
      ocustCodeList.push(listCompanyCusts[i].custCode)
    }
    this.listCompanyCusts = listCompanyCusts
    this.ocustNameList = ocustNameList
    this.ocustCodeList = ocustCodeList
  })
}
// 列表商家所有商品基本信息
export function getListCompanyGoods() {
  get(api.getListCompanyGoods).then(res => {
    var listCompanyGoods = res.data.data
    var odSkuCodeList = []
    var odSpecList = []
    for (const i in listCompanyGoods) {
      odSkuCodeList.push(listCompanyGoods[i].code)
      odSpecList.push(listCompanyGoods[i].spec)
    }
    this.odSkuCodeList = odSkuCodeList
    this.odSpecList = odSpecList
  })
}
