import infiniteScroll from 'vue-infinite-scroll'
import { postJson, get } from '@/api/axios.js'
import api from '@/api/index.js'
import moment from 'moment'
import companyList from './child_lib/companyList'
import dispatchInfo from '@/views/product/pdm/goods/child_info/GoodsInfo'
import { mixinBasic, mixinDrawer } from '@/components/tools/mixin/mixin'
import { mixinCurrentPage, mixinGetProductAbout } from '@/views/sale/mixin/minxinSale'
export default {
  directives: { infiniteScroll },
  mixins: [mixinBasic, mixinDrawer, mixinCurrentPage, mixinGetProductAbout],
  components: {
    companyList,
    dispatchInfo
  },
  data() {
    return {
      factoryId: '',
      dateFormat: 'YYYY-MM-DD',
      busy: false,
      companyVisible: false,
      loadingCompony: false,
      dataCompanySize: 0,
      dataCompany: [],
      factoryOrderDetailPostBean: {
        currentPage: 1,
        pageSize: 15,
        cz: '',
        spec: '',
        startTime: '',
        endTime: ''
      },
      companyName: '请选择工厂',
      companyList: [],
      companyNameList: [],
      updateList: {
        num: 0,
        date: ''
      },
      submitLoading: false,
      timer: '',
      goodsViewInfo: {}
    }
  },
  created() {
    this.getListCompanySpringCz(this.specCurrentPage)
    this.getListCompanySpringSpecs(this.czCurrentPage)
  },
  methods: {
    moment,
    // 按条件搜索渠道商订单列表
    inputChange(val, type) {
      this.dataCompany = []
      const factoryOrderDetailPostBean = this.factoryOrderDetailPostBean
      factoryOrderDetailPostBean.currentPage = 1
      if (type === 'date') {
        factoryOrderDetailPostBean.startTime = val[0]
        factoryOrderDetailPostBean.endTime = val[1]
      } else {
        factoryOrderDetailPostBean[type] = val
      }
      if (this.timer !== '') {
        clearTimeout(this.timer)
      }
      this.timer = setTimeout(() => {
        this.getFactoryOrder()
      }, 1000)
    },
    // 滚动滑动条加载订单
    handleInfiniteOnLoad() {
      const data = this.dataCompany
      this.loadingCompony = true
      this.busy = true
      if (data.length >= this.dataCompanySize && this.dataCompanySize !== 0) {
        this.busy = true
        this.loadingCompony = false
      } else {
        this.getFactoryOrder()
        this.busy = false
        this.loadingCompony = false
      }
    },
    // 移除订单
    removeItem(id, index) {
      var _this = this
      this.$confirm({
        title: '移除订单信息',
        content: '您确定要移除这条订单消息吗?',
        zIndex: 99999,
        onOk() {
          postJson(api.delFactoryOrder, { id: id }).then(res => {
            if (res.data.code === 0) {
              _this.$message.success('移除成功', 1)
              _this.dataCompany.splice(index, 1)
              _this.dataCompanySize--
            }
          })
        },
        onCancel() { }
      })
    },
    // 打开抽屉
    showDrawer() {
      this.companyVisible = !this.companyVisible
    },
    // 关闭抽屉
    onClose() {
      this.companyVisible = false
    },
    // 分派订单到工厂
    addCompany(id, index, type) {
      const dataCompany = this.dataCompany
      const companyList = this.companyList
      const updateList = this.updateList
      this.dataCompany[index].clickedVisible = false
      const tar = companyList.filter(item => item.id === id)[0]
      if (tar) {
        if (type === 'addAll') {
          tar.odNumber += dataCompany[index].odNumber
          this.dataCompanySize--
          dataCompany.splice(index, 1)
        } else {
          if (updateList.num >= dataCompany[index].odNumber) {
            this.dataCompanySize--
            dataCompany.splice(index, 1)
          } else {
            dataCompany[index].odNumber -= updateList.num
          }
          tar.odPromiseDueDate = updateList.date
          tar.odNumber += updateList.num
        }
      } else {
        if (type === 'addAll') {
          companyList.push(dataCompany[index])
          dataCompany.splice(index, 1)
          this.dataCompanySize--
        } else {
          const target = dataCompany[index]
          if (updateList.num >= target.odNumber) {
            target.odPromiseDueDate = updateList.date
            companyList.push(target)
            dataCompany.splice(index, 1)
            this.dataCompanySize--
          } else {
            const team = JSON.parse(JSON.stringify(target))
            target.odNumber -= updateList.num
            team.odNumber = updateList.num
            team.odPromiseDueDate = updateList.date
            companyList.push(team)
          }
        }
      }
    },
    // 删除工厂中的订单
    minusCompany(id, index) {
      const dataCompany = this.dataCompany
      const companyList = this.companyList
      const target = dataCompany.filter(item => item.id === id)[0]
      if (target) {
        target.odNumber += companyList[index].odNumber
      } else {
        dataCompany.push(companyList[index])
      }
      companyList.splice(index, 1)
    },
    // 点击取消
    handleClose() {
      this.factoryOrderDetailPostBean.currentPage = 1
      this.dataCompany = []
      this.companyList.splice(0)
      this.getFactoryOrder()
    },
    // 获取渠道商订单列表
    getFactoryOrder() {
      postJson(api.getFactoryOrder, this.factoryOrderDetailPostBean).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records
          if (data.length === 0) {
            this.$message.warning('暂时没有更多订单数据')
            return
          }
          data.forEach(item => {
            item.clickedVisible = false
          })
          this.dataCompanySize = Number(res.data.data.total)
          this.dataCompany = this.dataCompany.concat(data)
          this.factoryOrderDetailPostBean.currentPage += 1
        }
      })
    },
    // 设置选中的订单产品的数量和交期
    updataDes(num, data, index) {
      const updateList = this.updateList
      updateList.num = num
      updateList.date = data
      this.dataCompany[index].clickedVisible = true
    },
    updateTime(dates, dateStrings) {
      this.updateList.date = dateStrings
    },
    // 订单分派提交
    submit() {
      if (this.companyList.length === 0) {
        return
      }
      if (this.factoryId === '') {
        this.$message.warning('请选择工厂')
        return
      }
      const addFactorySaleOrderPostBean = {
        factoryId: this.factoryId,
        list: []
      }
      const list = addFactorySaleOrderPostBean.list
      const companyList = this.companyList
      companyList.forEach(item => {
        list.push(
          {
            id: item.id,
            number: item.odNumber,
            orderNumber: item.orderNumber,
            odRequireDueDate: item.odPromiseDueDate
          }
        )
      })
      this.submitLoading = true
      postJson(api.addFactoryOrder, addFactorySaleOrderPostBean).then(res => {
        this.submitLoading = false
        if (res.data.code === 0) {
          this.$message.success('分派成功')
          this.factoryOrderDetailPostBean.currentPage = 1
          this.companyList = []
          this.dataCompany = []
          this.getFactoryOrder()
        } else {
          this.$message.error(res.data.msg)
        }
      })
    },
    // 替换显示工厂名字以及工厂id
    selectName(data) {
      this.factoryId = data.id
      this.companyName = data.name
      this.companyVisible = false
    },
    // 关闭抽屉
    isDrawer(val) {
      this.visible = val
    },
    // 查看产品详情
    infoView(id) {
      this.getViewInfo(id)
      this.$store.commit('setPromptTitle', {
        title: '产品信息',
        isEdit: true
      })
    },
    getViewInfo(params) {
      get(api.goodsView + `?skuId=${params}`).then(res => {
        if (res.data.code === 0) {
          this.goodsViewInfo = res.data.data
          this.visible = true
        }
      }).catch(err => {
        console.log(err)
      })
    },
    // 选择框的下拉加载
    currentPageChange(e, type) {
      const result = this.getCurrentPageChange(e)
      if (result) {
        if (type === 'cz') {
          this.getListCompanySpringCz(this.czCurrentPage)
        } else if (type === 'spec') {
          this.getListCompanySpringSpecs(this.specCurrentPage)
        }
      }
    }
  }
}
