import api from '@/api/index.js'
import moment from 'moment'
import 'moment/locale/zh-cn'
import { mixinGetProductAbout, mixinCurrentPage } from '@/views/sale/mixin/minxinSale'
moment.locale('zh-cn')
export default {
  props: {
    typeInput: {
      type: String,
      default: '',
      required: true
    }
  },
  mixins: [mixinGetProductAbout, mixinCurrentPage],
  data() {
    return {
      dateArr: [],
      timer: '',
      data: {},
      companyType: localStorage.getItem('companyType')
    }
  },
  created() {
    this.getSpec()
  },
  methods: {
    // 按条件搜索
    inputChange(val, type) {
      if (type === 'date') {
        this.data.startTime = val[0]
        this.data.endTime = val[1]
      } else {
        this.data[type] = val
      }
      // 输入框防抖
      if (this.timer !== '') {
        clearTimeout(this.timer)
      }
      this.timer = setTimeout(() => {
        this.$emit('dataChange', this.data)
      }, 500)
    },
    // 重置搜索条件
    searchReset() {
      this.data.odOrderNumber = ''
      this.data.spec = ''
      this.dateArr = []
      this.data.endTime = ''
      this.data.startTime = ''
      this.$emit('dataChange', this.data)
    },
    currentPageChange(e) {
      const result = this.getCurrentPageChange(e)
      if (result) {
        this.getSpec(this.specCurrentPage)
      }
    },
    handleSelectChange(val) {
      if (this.timer !== '') {
        clearTimeout(this.timer)
      }
      this.timer = setTimeout(() => {
        this.searchConditions(val)
      }, 500)
    },
    searchConditions(val) {
      this.odSpecList = []
      this.odSpecListValue = val
      this.getSpec()
    },
    /**
    * @description spec的区别获取
    * @param distributors 渠道商
    * @param factory 工厂
    * @param procurement 采购列表/供应商
    */
    getSpec(specCurrentPage = 1) {
      if (this.typeInput === 'distributors') {
        this.getListCompanySpringSpecs(specCurrentPage)
      } else if (this.typeInput === 'factory') {
        this.getFactoryPolicyOrderSpec(specCurrentPage)
      } else if (this.typeInput === 'procurement') {
        this.getFactoryMaterialRequireSpec(specCurrentPage)
      }
    }
  }
}
