import {
  getGoodsList,
  getListCompanyCusts,
  getCustList,
  postCategoryAdd,
  detailInfoShow,
  deleteCategory,
  onSave
} from './getCategory.js'
import { setTimeout } from 'timers'
import { recursive } from 'utils/utils.js'
import Title from '@/assets/common/Title.vue'

export default {
  name: 'CateGoryInfo',
  data() {
    return {
      loading: false,
      cateGoryInfo: this.$form.createForm(this),
      value: 1,
      categoryName: true,
      isCategorySuperior: false,
      isDisabled: false,
      selectOptions: [],
      options: [],
      timer: null,
      getViewCategoryInfo: null,
      categorySuperiorContent: '',
      categoryEdit: '点击编辑',
      isInfoDisplay: {
        display: 'none'
      },
      isLabelColor: false
    }
  },
  props: {
    isEdit: {
      type: Boolean,
      default: false
    },
    viewCategoryInfo: {
      type: Object,
      default: function() {
        return {}
      }
    }
  },
  created() {
    // 公司分类获取所有上级分类
    getGoodsList.call(this)
    // 所属客户
    getListCompanyCusts.call(this)
  },
  mounted() {
    // 设置默认值
  },
  beforeDestroy() {
    // 清空定时器
    clearTimeout(this.timer)
  },
  watch: {
    viewCategoryInfo: {
      handler: function(val) {
        this.getViewCategoryInfo = val
        this.showViewCategory()
      },
      deep: true
    }
  },
  computed: {
    isCategoryEdit() {
      return this.isEdit
    }
  },
  methods: {
    // 产品分类详情信息展示
    showViewCategory() {
      // 上级分类隐藏域
      this.isCategorySuperior = true
      this.isDisabled = true
      this.isLabelColor = true
      // 清空数组
      this.options = []
      console.log(this.getViewCategoryInfo.custId)
      // // 判断是否编辑状态
      if (this.isCategoryEdit) {
        // 显示客户分类
        if (Number(this.getViewCategoryInfo.custId) !== 0) {
          // 获取客户上级分类
          // getCustList.call(this, this.getViewCategoryInfo.custId)
          // radio
          this.value = 2
          // 公司|客户分类
          this.categoryName = false
          // 查找客户所属名称
          this.timer = setTimeout(() => {
            const customerFrom = this.selectOptions.filter(item => {
              return item.custId === this.getViewCategoryInfo.custId
            })
            console.log(customerFrom[0].name)
            console.log(this.getViewCategoryInfo.pid)
            // // 根据pid查找上级分类所在的对象
            // recursive.call(this, this.options, this.getViewCategoryInfo.pid)
            // console.log(this.categorySuperiorContent)
            // 客户分类详情回填
            detailInfoShow.call(
              this,
              this.getViewCategoryInfo.remark === null
                ? ''
                : this.getViewCategoryInfo.remark,
              this.getViewCategoryInfo.name,
              customerFrom[0].name,
              this.getViewCategoryInfo.parentClassname
            )
          }, 200)
        }
        // 显示公司分类
        if (Number(this.getViewCategoryInfo.custId) === 0) {
          this.value = 1
          // 获取上级分类总列表
          getGoodsList.call(this)
          this.timer = setTimeout(() => {
            // 根据pid查找上级分类所在的对象
            console.log(this.options)
            console.log(this.getViewCategoryInfo.pid)
            // recursive.call(this, this.options, this.getViewCategoryInfo.pid)
            // console.log(this.categorySuperiorContent)
            // 公司分类详情回填
            detailInfoShow.call(
              this,
              this.getViewCategoryInfo.remark === null
                ? '无备注'
                : this.getViewCategoryInfo.remark,
              this.getViewCategoryInfo.name,
              null,
              this.getViewCategoryInfo.parentClassname
            )
          }, 200)
        }
      }
    },
    // 公司客户分类radio
    onChangeCategoryRadio(e) {
      console.log(e.target.value)
      if (e.target.value === 1) {
        this.categoryName = true
        getGoodsList.call(this)
        return false
      }
      this.categoryName = false
      getListCompanyCusts.call(this)
    },
    // 一级新建列表遮罩显示
    onClose() {
      this.isVisible = false
      this.$emit('isVisible', this.isVisible)
    },
    // 新增信息提交
    onSubmit(e) {
      e.preventDefault()
      this.loading = true
      this.cateGoryInfo.validateFields((err, values) => {
        if (!err) {
          console.log(values)
          const params = {
            catType: 0,
            name: values.categoryClassName || '',
            pid: values.categorySuperiorOK
              ? values.categorySuperiorOK.slice(-1).join('')
              : 0,
            custId: values.categoryCustomer || 0,
            remark: values.categoryRemarks || ''
          }
          console.log(params)
          postCategoryAdd.call(this, params)
          this.loading = false
          return false
        }
        this.loading = false
      })
    },
    // 上级分类
    onChangeCategoryName(value) {
      console.log(value)
    },
    // 所属客户
    onSelectCategoryName(value) {
      console.log(value)
      getCustList.call(this, value)
    },
    // 详情页编辑
    onHandleCategoryEdit() {
      // 上级分类隐藏域
      if (this.categoryEdit === '点击编辑') {
        this.isCategorySuperior = false
        this.isDisabled = false
        this.categoryEdit = '取消编辑'
        this.isInfoDisplay.display = 'block'
        this.isLabelColor = false
        return false
      }
      this.isCategorySuperior = true
      this.isDisabled = true
      this.isInfoDisplay.display = 'none'
      this.isLabelColor = true
      this.categoryEdit = '点击编辑'
    },
    // 删除
    onhandleDeleteCategoty() {
      const that = this
      this.$confirm({
        title: '删除',
        content: '您确认要删除？',
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          deleteCategory.call(that, that.getViewCategoryInfo.id)
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    // 保存修改
    onEditSave(e) {
      e.preventDefault()
      this.loading = true
      this.cateGoryInfo.validateFields((err, values) => {
        if (!err) {
          console.log(values)
          const params = {
            name: values.categoryClassName || '',
            pid: values.categorySuperiorOK
              ? values.categorySuperiorOK.slice(-1).join('')
              : 0,
            custId: this.getViewCategoryInfo.custId || 0,
            remark: values.categoryRemarks || '',
            id: this.getViewCategoryInfo.id
          }
          console.log(params)
          onSave.call(this, params)
          this.loading = false
          return false
        }
        this.loading = false
      })
    }
  },
  components: {
    Title
  }
}
