import { getGoodsTree, searchGoods } from './goods.js'
import GoodsInfo from './GoodsInfo'

export default {
  name: 'goods',
  data() {
    return {
      scrollLen: { x: 300 },
      title: '产品信息',
      // table
      tabData: [],
      columns: [
        {
          title: '产品编码',
          dataIndex: 'code',
          key: 'code',
          width: 106,
          fixed: 'left'
        },
        {
          title: '上下架',
          dataIndex: 'saleStatus',
          key: 'saleStatus',
          width: 106,
          fixed: 'left'
        },
        {
          title: '产品名称',
          dataIndex: 'name',
          key: 'name',
          width: 106,
          scopedSlots: { customRender: 'name' },
          fixed: 'left'
        },
        {
          title: '规格',
          dataIndex: 'spec',
          key: 'spec',
          width: 106,
          fixed: 'left'
        },
        {
          title: '单位',
          dataIndex: 'nuit',
          key: 'nuit',
          width: 106,
          fixed: 'left'
        },
        {
          title: '客户编码',
          dataIndex: 'customCode',
          key: 'customCode',
          width: 106
        },
        {
          title: '价格',
          dataIndex: 'price',
          key: 'price',
          width: 106
        },
        {
          title: '时间',
          key: 'creatTime',
          width: 106,
          dataIndex: 'creatTime',
          fixed: 'right'
        }
      ],
      loading: false,
      visible: false,
      treeData: [],
      addCloumn: [],
      isCatId: '',
      isCustId: '',
      addGoodsId: ''
    }
  },
  created() {
    getGoodsTree.call(this)
  },
  mounted() {
    // 初始化客户信息
    // showCustomersPage.call(this)
  },
  methods: {
    // tree
    onSelect(selectedKeys, info) {
      console.log(info.node.dataRef.custundefined)
      this.isCustId = info.node.dataRef.custundefined === null ? '0' : info.node.dataRef.custundefined
      // 判断是否存在字母
      const isLetter = /[a-z]/i
      if (!isLetter.test(selectedKeys[0])) {
        const params = {
          catIds: selectedKeys[0],
          pageNo: '1',
          pageSize: '999'
        }
        this.isCatId = selectedKeys[0]
        searchGoods.call(this, params)
      }
    },
    onExpand() {
      console.log('Trigger Expand')
    },
    onDeleteProgoods() {},
    showDrawer() {
      if (this.isCatId !== '') {
        this.visible = true
        return false
      }
      this.$message.error('请先选择分类')
    },
    onClose() {
      this.visible = false
    },
    // $emit改变isvisible
    isVisible(isVisible) {
      this.visible = isVisible
    }
  },
  components: {
    GoodsInfo
  }
}
