/**
 * @props {String} companyType 公司的类型用来搜索不同的材质和规格
 * @returns 返回搜索的信息
 */
import moment from 'moment'
import { mixinBasic } from '@/components/tools/mixin/mixin'
import { mixinCurrentPage, mixinGetProductAbout } from '@/views/sale/mixin/minxinSale'
export default {
  mixins: [mixinBasic, mixinCurrentPage, mixinGetProductAbout],
  props: ['companyType'],
  data() {
    return {
      timer: '',
      data: {
        cz: '',
        spec: '',
        startTime: '',
        endTime: ''
      }
    }
  },
  created() {
    this.getCz()
    this.getSpec()
  },
  methods: {
    moment,
    // 按条件搜索渠道商订单列表
    inputChange(val, type) {
      this.dataCompany = []
      const data = this.data
      if (type === 'date') {
        data.startTime = val[0]
        data.endTime = val[1]
      } else {
        data[type] = val
      }
      if (this.timer !== '') {
        clearTimeout(this.timer)
      }
      this.timer = setTimeout(() => {
        this.$emit('changeInput', this.data)
      }, 500)
    },
    // 选择框的下拉加载
    currentPageChange(e, type) {
      const result = this.getCurrentPageChange(e)
      if (result) {
        if (type === 'cz') {
          this.getCz(this.czCurrentPage)
        } else if (type === 'spec') {
          this.getSpec(this.specCurrentPage)
        }
      }
    },
    // 模糊搜索
    handleSelectChange(val, type) {
      if (this.timer !== '') {
        clearTimeout(this.timer)
      }
      this.timer = setTimeout(() => {
        if (type === 'cz') {
          this.textures = []
          this.texturesValue = val
          this.getCz()
        } else {
          this.odSpecList = []
          this.odSpecListValue = val
          this.getSpec()
        }
      }, 500)
    },
    // 获取材质
    getCz(current = 1) {
      if (this.companyType === 'FACTORY') {
        this.getFactoryPolicyOrderCz(current)
      } else {
        this.getListCompanySpringCz(current)
      }
    },
    // 获取规格
    getSpec(current = 1) {
      if (this.companyType === 'FACTORY') {
        this.getFactoryPolicyOrderSpec(current)
      } else {
        this.getListCompanySpringSpecs(current)
      }
    }
  }
}
