import infiniteScroll from 'vue-infinite-scroll'
import orderInfo from '../orderList/child_lib/orderDetail'
import Bus from '@/Bus.js'
import { get, postJson, put } from '@/api/axios.js'
import api from '@/api/index.js'
import moment from 'moment'
import { mixinBasic } from '@/components/tools/mixin/mixin'
import { mixinCurrentPage, getListCompanyCusts } from '@/views/sale/mixin/minxinSale'
export default {
  directives: { infiniteScroll },
  components: {
    orderInfo
  },
  mixins: [mixinBasic, mixinCurrentPage, getListCompanyCusts],
  data() {
    return {
      busy: false,
      companyVisible: false,
      loadingCompony: false,
      dataCompanySize: 0,
      dataCompany: [],
      currentPage: 1,
      pageSize: 15,
      custName: '',
      companyList: [],
      selectIndex: '',
      submitLoading: false,
      companyType: '',
      visible: false,
      detialList: '',
      editInfo: true
    }
  },
  created() {
    this.companyType = localStorage.getItem('companyType')
  },
  computed: {
    getMenuPermission() {
      return this.$store.state.menuPermission
    }
  },
  methods: {
    moment,
    inputChange(value) {
      this.dataCompany = []
      this.custName = this.listCompanyCusts[value].custShortName
      if (this.timer !== '') {
        clearTimeout(this.timer)
      }
      this.timer = setTimeout(() => {
        this.getFactoryOrder()
      }, 1000)
    },
    handleSelectChange(value) {
      this.listCompanyCusts = []
      this.condition = value
      this.getListCompanyCusts()
    },
    handleInfiniteOnLoad() {
      const data = this.dataCompany
      this.loadingCompony = true
      this.busy = true
      if (data.length >= this.dataCompanySize && this.dataCompanySize !== 0) {
        this.busy = true
        this.loadingCompony = false
      } else {
        this.getFactoryOrder()
        this.busy = false
        this.loadingCompony = false
      }
    },
    removeItem(id, index) {
      const _this = this
      const data = []
      data.push({
        id,
        odeliveryStatus: 'NONEEDDELLIVERY'
      })
      this.$confirm({
        title: '移除发货信息',
        content: '您确定要移除这条发货消息吗?',
        zIndex: 99999,
        onOk() {
          put(api.outStorageApplyStatusChange, data).then(res => {
            if (res.data.code === 0) {
              _this.$message.success('移除成功')
              _this.dataCompany.splice(index, 1)
              _this.dataCompanySize--
            } else {
              _this.$message.error(res.data.msg)
            }
          })
        },
        onCancel() { }
      })
    },
    addCompany(id, index) {
      this.selectIndex = index
      const dataCompany = this.dataCompany
      const companyType = this.companyType
      if (companyType === 'CHANNEL') {
        this.companyList = dataCompany[index].skuList
      } else {
        this.companyList = [dataCompany[index]]
      }
    },
    minusCompany(id, index) {
    },
    getFactoryOrder(currentPage = 1, pageSize = 15) {
      const data = {}
      data.currentPage = currentPage
      data.pageSize = pageSize
      data.custName = this.custName
      get(api.outStorageApplyTobedistributed, data).then(res => {
        if (res.data.code === 0) {
          const data = res.data.data.records ? res.data.data.records : []
          if (this.companyType === 'CHANNEL') {
            data.forEach(item => {
              item.clickedVisible = false
              item['requireLevel'] = false
              item.skuList.forEach(itm => {
                itm['fNum'] = itm.odNumber
              })
            })
          } else {
            data.forEach(item => {
              item.clickedVisible = false
              item['requireLevel'] = false
              item['fNum'] = item.odNumber
            })
          }
          this.dataCompanySize = Number(res.data.data.total)
          this.dataCompany = this.dataCompany.concat(data)
          this.currentPage += 1
        }
      })
    },
    updataDes(e, index) {
      window.event ? window.event.cancelBubble = true : e.stopPropagation()
      this.dataCompany[index].clickedVisible = true
    },
    submit() {
      const companyList = this.companyList
      let deliverySure = false
      if (companyList.length === 0) { return false }
      if (this.companyType === 'CHANNEL') {
        companyList.forEach(item => {
          if (item.bomGroupId && item.craftGroupId && item.skuPriceInventory) {
            if (item.fNum > item.skuPriceInventory.storeCount) {
              deliverySure = true
            }
          } else {
            if (item.fNum > item.sku.storeCount) {
              deliverySure = true
            }
          }
        })
      } else {
        companyList.forEach(item => {
          if (item.skuPriceInventory && item.fNum > item.skuPriceInventory.storeCount) {
            deliverySure = true
          }
        })
      }
      if (deliverySure) {
        this.prompt()
      } else {
        const data = this.dataSplic()
        this.deliveryRequest(data)
      }
    },
    // 发货请求
    deliveryRequest(data) {
      this.submitLoading = true
      postJson(api.outStorageApplyOpt, data).then(res => {
        if (res.data.code === 0) {
          this.dataCompany = []
          this.companyList = []
          this.selectIndex = ''
          this.getFactoryOrder()
          this.$message.success('发货成功')
        } else {
          this.$message.error(res.data.msg)
        }
        this.submitLoading = false
      }).catch(() => {
        this.submitLoading = false
      })
    },
    // 发货数据处理
    dataSplic() {
      const data = []
      const companyList = this.companyList
      const dataCompany = this.dataCompany[this.selectIndex]
      if (this.companyType === 'CHANNEL') {
        companyList.forEach(item => {
          data.push(
            {
              oid: dataCompany.id,
              odId: item.id,
              fromSkuId: item.skuId,
              requireLevel: dataCompany.requireLevel ? 'LEVEL1' : 'LEVEL0',
              reciverId: item.odCustId,
              reciverName: dataCompany.ocustShortName,
              oaddresseeName: dataCompany.oaddresseeName,
              ophone: dataCompany.ophone,
              oaddress: dataCompany.olocation + dataCompany.ostreet,
              outCount: item.fNum,
              fromSkuName: item.odSkuName,
              fromSkuSpec: item.odSpec,
              relateOrderSkuCount: item.odNumber,
              packageInfo: ''
            }
          )
        })
      } else {
        companyList.forEach(item => {
          data.push({
            oid: dataCompany.id,
            odId: item.ssodId,
            requireLevel: dataCompany.requireLevel ? 'LEVEL1' : 'LEVEL0',
            reciverId: item.factoryId,
            reciverName: item.factoryName,
            toSkuId: item.factorySkuId,
            fromSkuId: item.supplierSkuId,
            fromSkuName: item.fromSkuName,
            toSkuName: item.toSkuName,
            toSkuSpec: item.toSkuSpec,
            fromSkuSpec: item.fromSkuSpec,
            outCount: item.fNum,
            relateOrderSkuCount: item.odNumber,
            packageInfo: '',
            oaddresseeName: item.factoryName,
            ophone: '',
            oaddress: ''
          })
        })
      }
      return data
    },
    // 库存不足发货提示
    prompt() {
      const _this = this
      this.$confirm({
        title: '通知',
        content: '库存量不足，是否还要发货？',
        onOk() {
          const data = _this.dataSplic()
          _this.deliveryRequest(data)
        },
        onCancel() {
        }
      })
    },
    cancelSubmit() {
      this.companyList = []
      this.selectIndex = []
    },
    currentPageChange(e) {
      const result = this.getCurrentPageChange(e)
      if (result) {
        this.getListCompanyCusts(this.customerPage)
      }
    },
    infoView(e, id) {
      window.event ? window.event.cancelBubble = true : e.stopPropagation()
      this.detialList = id
      this.$store.commit('setPromptTitle', {
        title: '销售订单详情',
        isEdit: true
      })
      this.visible = true
    },
    // 关闭新建订单/详情抽屉
    isVisible(isVisible) {
      this.visible = false
      if (isVisible) {
        this.dataCompany = []
        this.companyList = []
        this.getFactoryOrder()
      }
    },
    // 关闭新建/详情订单抽屉
    onCustomerInfo() {
      this.visible = false
    }
  }
}
