import { mixinBasic, mixinIsEdit } from '@/components/tools/mixin/mixin'

export default {
  name: 'SelectComponent',
  mixins: [mixinBasic, mixinIsEdit],
  data() {
    return {
      selectData: {},
      getData: {}
    }
  },
  props: {
    data: {
      type: Object,
      default: {}
    },
    childDisabled: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    onChangeSelect(value, data) {
      console.log(value)
      this.dataValue = value
      console.log(data)
      Object.assign(this.selectData, {
        catAttrId: data.data.id,
        catAttrName: data.data.name,
        attrValId: value.key,
        val: value.label
      })
      this.$emit('categoryData', this.selectData)
    }
  },
  watch: {
    data: {
      handler(val) {
        this.getData = val
      },
      deep: true,
      immediate: true
    }
  }
}
